import sysimport osdef main(*args):	"""	Renames files from adobe batch	"""		os.chdir("C:/Users/thomasuster/War_Game/images/units")	images = []	for root, dirs, files in os.walk(os.getcwd()):		images = files	for image in images:		import re		#Get query string		exp = r'(.*) copy.png'		p = re.compile(exp)				src = image;		dst = p.sub(r'\1.png',image)		os.rename(src, dst)if __name__ == '__main__':    sys.exit(main(*sys.argv))